// 7. Output the ID of the selected event on the EventDetailPage
// BONUS: Add another (nested) layout route that adds the <EventNavigation> component above all /events... page components
import { RouterProvider, createBrowserRouter } from 'react-router-dom';
import { HomePage } from './pages/Home';
import EventsPage from './pages/Events';
import { EventDetailPage } from './pages/EventDetail';
import { NewEventPage } from './pages/NewEvents';
import { EditEventPage } from './pages/EditEvent';
import { RootLayout } from './pages/Root';
import { EventsRootLayout } from './pages/EventsRoot';
import { loader as eventsLoader } from './pages/Events';
import { loader as eventDetailLoader, action as deleteEventDetailAction } from './pages/EventDetail'
import { ErrorPage } from './pages/Error';
import { action as manipulateEventAction } from './components/EventForm'

const router = createBrowserRouter([
  {
    path: '/', element: <RootLayout />,
    errorElement: <ErrorPage />,
    children: [
      { index: true, element: <HomePage /> },
      {
        path: 'events', element: <EventsRootLayout />, children: [
          {
            index: true,
            element: <EventsPage />,
            loader: eventsLoader
          },
          {
            path: ':eventId', id: 'event-detail', loader: eventDetailLoader, children: [
              { index: true, element: <EventDetailPage />, action: deleteEventDetailAction },
              { path: 'edit', element: <EditEventPage /> }
            ]
          },
          { path: 'new', element: <NewEventPage />, action: newEventAction },
        ]
      }
    ]
  },
]);

function App() {
  return <RouterProvider router={router} />;
}

export default App;
